#include <iostream> using namespace std; void solve() {    long long n, k;    if (!(cin >> n >> k)) return;     if (n % 2 == 0) {        cout << "YES" << endl;    }     // Case 2: n is odd - possible only if k is also odd    else {        if (k % 2 != 0) {            cout << "YES" << endl;        } else {            cout << "NO" << endl;        }    }} int main() {    // Speed up I/O for Codeforces    ios::sync_with_stdio(false);    cin.tie(nullptr);     int T;     cin >> T;    while (T--) {        solve();    }    return 0;}